import "../css/index.css"
import unfav from "../images/unfav.svg"
import house from "../images/house.svg"
import { useState } from "react"

const Unfavourite_item = ({ hotel, name, choosedDate, countOfDays }) => {
    const favImg = document.querySelector(".favoutite-hotels-list__fav")
    const favUnfav = () => {
        favImg.classList.toggle("favoutite-hotels-list__fav")
        console.log('kekius')
    }
    const [tripDays, setTripDays] = useState("1 день")
    if (countOfDays)
    // switch (tripDays) {
