import React, { useState, useEffect } from "react";

const UnfavouriteHotelsList = ({ choosedLocation, choosedDate, countOfDays }) => {
    const link = `https://engine.hotellook.com/api/v2/lookup.json?query=${choosedLocation}&lang=en&lookFor=hotel&limit=${countOfDays}`
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [hotels, setHotels] = useState([]);
    useEffect(() => {
        fetch(`https://engine.hotellook.com/api/v2/lookup.json?query=${choosedLocation}&lang=en&lookFor=hotel&limit=${countOfDays}`)
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setHotels(result.results);

                },
                (error) => {
                    setIsLoaded(true);
                    setHotels(error);
                }
            )
    }, [choosedLocation])
    console.log(link)
    if (error) {
        return(
            <div className="favoutite-hotels-list">
                <div>
                    Ошибка:
                    {/* {error.message} */}
                </div>
            </div>
        )
    } else if (!isLoaded) {
        return (
            <div className="favoutite-hotels-list">
                <div>Загрузка...</div>
            </div>
        )
    } else if (choosedLocation) {
        return (
            <div className="favoutite-hotels-list">
                {hotels.map(hotel => (
                    <div key={hotel.id}>
                        {hotel.label}
                    </div>
                ))}
            </div>
        );
    }
}

export default UnfavouriteHotelsList;
