    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    useEffect(() => {
        fetch(`https://engine.hotellook.com/api/v2/lookup.json?query=${choosedLocation}&lang=en&lookFor=hotel&limit=${countOfDays}`)
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                (error) => {
                    setIsLoaded(true);
