import "../css/main-page.css";
import exit from "../images/exit.svg";
import fullDate from "../js/date";
import Location from "../components/Location_Input";

const Main = () => {
    const locationsStyle = {
        height: "50px",
        "padding-right": "20px",
    };

    return (
        <div className="container">
            <div className="header_box">
                <h2 className="title">
                    Simple Hotel Check
                </h2>
                <p className="exit">
                    Выход
                    <img src={exit} alt="exit" />
                </p>
            </div>
            <div className="main">
                <div className="main_box">
                    <div className="main__first-column">
                        <div className="main-search-data__box">
                            <form className="search_form">
                                <p>Локация</p>
                                <div className="location" >
                                    <Location style={locationsStyle} />
                                </div>
                                <p>Дата заселения</p>
                                <input type="date" name="trip-start"
                                    placeholder={fullDate}
                                    min={fullDate} max = "2029-01-01"/>
                                <p>Количество дней</p>
                                <input name="trip-durations" type="number" placeholder="1" min="1" max="90" />
                                <button type="submit">Найти</button>
                            </form>
                        </div>
                        <div className="main-favourite__box">
                        </div>
                    </div>
                    <div className="main__second-column">
                    </div>
                </div>
            </div>
        </div>
    );
};
export default Main;
